/* 
    Declare all *.jpg, *.png and *.svg files as modules.
 */

declare module "*.jpg" {
  const content: any;
  export default content;
}

declare module "*.png" {
  const content: any;
  export default content;
}

declare module "*.svg" {
  const content: any;
  export default content;
}

declare module "*.tsv" {
  const content: [any];
  export default content;
}
